#include <iostream> 
using namespace std; 
int main() {
    int arr[6] = {10, 20, 30, 40, 50, 90}; // Declare and initialize an array with 6 elements
    // Loop through the array and print each element
    for (int i = 0; i <=6; i++) {
        cout << "Element at index:" << i <<":"<< arr[i] << endl; // Print each array element
    }
    return 0; 
}

